mapper.select:
WITH RECURSIVE CategoryTree AS ( SELECT category_id, parent_id, category_name, description, sort_order, created_time, updated_time FROM category WHERE category_id = #{categoryId}
UNION ALL
SELECT c.category_id, c.parent_id, c.category_name, c.description, c.sort_order, c.created_time, c.updated_time FROM category c INNER JOIN CategoryTree ct ON c.parent_id = ct.category_id ) SELECT * FROM CategoryTree;
