mapper.select:
SELECT cg.category_name, cp.competition_id, cp.competition_img_url, cp.competition_name, cp.competition_status, cp.created_time, cp.updated_time, cp.is_active, cpl.level_name FROM competition AS cp INNER JOIN category AS cg ON cp.category_id = cg.category_id INNER JOIN competition_level AS cpl ON cp.level_id = cpl.level_id
AND cp.competition_name LIKE CONCAT('%', #{competitionName}, '%')
AND cp.category_id = #{categoryId}
AND cp.level_id = #{levelId}
AND cp.is_active = #{isActive}
ORDER BY cp.created_time DESC
